mapper.select:
SELECT * FROM t_info i INNER JOIN t_infotype it ON it.id = i.typeId
i.state = 1 AND i.payfor = 0 AND i.typeId = #{infoTypeId}
ORDER BY i.infoDate DESC

SELECT * FROM t_info i INNER JOIN t_infotype it ON i.typeId = it.id
i.id = #{id}
ORDER BY i.infoDate DESC

SELECT * FROM
t_info i INNER JOIN t_infotype it
ON i.typeId = it.id

	i.state = 1


			AND i.content like concat('%',#{keyword},'%')


			AND i.title like concat('%',#{keyword},'%')


			AND i.email like concat('%',#{keyword},'%')


			AND i.linkman like concat('%',#{keyword},'%')


			AND i.phone like concat('%',#{keyword},'%')



			AND i.content = #{keyword}


			AND i.title = #{keyword}


			AND i.email = #{keyword}


			AND i.linkman = #{keyword}


			AND i.phone = #{keyword}


			AND i.typeId = #{typeId}



ORDER BY i.infoDate DESC

SELECT * FROM
t_info i INNER JOIN t_infotype it
ON i.typeId = it.id


		AND i.state = #{state}


		AND payfor = #{payfor}


		AND i.typeId = #{typeId}


